import React from 'react'
import axios from 'axios'
import './App.css';
import {useState} from 'react'


export default function App() {

  const url = 'http://localhost/imdbback/'
  const [name, setName] = useState('');
  const [searchterm, setSearchterm] = useState([]);
  const [gname, setGname] = useState('');
  const [genre, setGenre] = useState([]);
 

  
  function getbyprhase(e) {

    e.preventDefault();    
    axios.get(url + 'searchbyterm.php/' + name)
    .then((response) => {
      const json = response.data;
      setSearchterm(json);
    }).catch (error => {
      if (error.response === undefined){
        alert(error);
      } else {
        alert(error.response.data.error);
      }
    })
  }
  function getgenre(e) {

    e.preventDefault();    
    axios.get(url + 'searchbygenre.php/' + name)
    .then((response) => {
      const json = response.data;
      setGenre(json);
    }).catch (error => {
      if (error.response === undefined){
        alert(error);
      } else {
        alert(error.response.data.error);
      }
    })
  }

  console.log(searchterm)

  return (
    <div className="container">
      <h2>Etsi elokuvia hakusanalla</h2>
      <div>
        <form onSubmit={getbyprhase}>
        <input className="form-control me-2" type="search" placeholder="Haku" aria-label="Search" 
        onChange={e => setName(e.target.value)}/>
        <button className="btn btn-outline-success" type="submit" >Etsi</button>
        </form>
        </div>                                             
       <div>
        <ul>
        {searchterm.map(item => (
          <li>Elokuvan nimi: {item.primary_title}<br /> Genre: {item.genre}</li>       
        ))}                                                                                 
        </ul>
      </div> 

      <h2>Etsi elokuvia genren mukaan</h2>
      <div>
        <form onSubmit={getgenre}>
        <input className="form-control me-2" type="search" placeholder="Haku" aria-label="Search" 
        onChange={e => setGname(e.target.value)}/>
        <button className="btn btn-outline-success" type="submit" >Etsi</button>
        </form>
        </div>                                             
       <div>
        <ul>
        {genre.map(item => (
          <li>Elokuvan nimi: {item.primary_title}<br /> Genre: {item.genre}</li>       
        ))}                                                                                 
        </ul>
      </div> 
    </div>
  )}
